setUp();		File reporegistry1 = getTestData("test data bug 300104bis", directory);		File tempFolder = getTempFolder();		copy("0.2", reporegistry1, tempFolder);		SimpleProfileRegistry realProfileRegistry = getProfileRegistry();		//Tweak the running profile registry		Field profileStore = SimpleProfileRegistry.class.getDeclaredField("store");		profileStore.setAccessible(true);		previousStoreValue = (File) profileStore.get(realProfileRegistry);		profileStore.set(realProfileRegistry, new File(tempFolder, "p2/org.eclipse.equinox.p2.engine/profileRegistry"));		Field profilesMapField = SimpleProfileRegistry.class.getDeclaredField("profiles"); //$NON-NLS-1$		profilesMapField.setAccessible(true);		profilesMapField.set(realProfileRegistry, null);		//End of tweaking the profile registry		profile = realProfileRegistry.getProfile(profileLoadedId);		assertNotNull(profile);		repo = loadMetadataRepository(getTestData("Repository for 300104bis", directory + "/repo").toURL());	}	private SimpleProfileRegistry getProfileRegistry() {		return (SimpleProfileRegistry) ServiceHelper.getService(TestActivator.getContext(), IProfileRegistry.class.getName());	}	protected IMetadataRepository loadMetadataRepository(URL location) throws ProvisionException {		IMetadataRepositoryManager metadataRepositoryManager = getMetadataRepositoryManager();		return metadataRepositoryManager.loadRepository(location, null);	}	protected void tearDown() throws Exception {		SimpleProfileRegistry realProfileRegistry = (SimpleProfileRegistry) getProfileRegistry();		Field profilesMapField = SimpleProfileRegistry.class.getDeclaredField("profiles"); //$NON-NLS-1$		profilesMapField.setAccessible(true);		profilesMapField.set(realProfileRegistry, null);		Field profileStore = SimpleProfileRegistry.class.getDeclaredField("store");		profileStore.setAccessible(true);		profileStore.set(realProfileRegistry, previousStoreValue);		super.tearDown();	}	public void testInstall() {		IMetadataRepositoryManager mgr = getMetadataRepositoryManager();		try {			repo = loadMetadataRepository(getTestData("test data bug 300104bis repo", directory + "/repo").toURL());		} catch (ProvisionException e) {			assertNull(e); //This guarantees that the error does not go unnoticed		} catch (MalformedURLException e) {			assertNull(e);		}		Collector ius = repo.query(InstallableUnitQuery.ANY, new Collector(), new NullProgressMonitor());		IPlanner planner = createPlanner();		ProvisioningPlan plan, expectedPlan = null;		plan = planner.getProvisioningPlan(createRequest(ius), null, new NullProgressMonitor());		expectedPlan = planner.getProvisioningPlan(createFilteredRequest(ius), null, new NullProgressMonitor());		Operand[] expectedOperands = expectedPlan.getOperands();		for (int i = 0; i < expectedOperands.length; i++) {			if (!(expectedOperands[i] instanceof InstallableUnitOperand))				continue;			IInstallableUnit iu = ((InstallableUnitOperand) expectedOperands[i]).second();			assertInstallOperand(plan, iu);		}	}	private ProfileChangeRequest createRequest(Collector ius) {		ProfileChangeRequest pcr = new ProfileChangeRequest(profile);		pcr.addInstallableUnits((IInstallableUnit[]) ius.toArray(IInstallableUnit.class));		Iterator it = ius.iterator();		while (it.hasNext()) {			IInstallableUnit iu = (IInstallableUnit) it.next();			pcr.setInstallableUnitInclusionRules(iu, PlannerHelper.createOptionalInclusionRule(iu));		}		return pcr;	}	private ProfileChangeRequest createFilteredRequest(Collector ius) {		ProfileChangeRequest pcr = new ProfileChangeRequest(profile);		Iterator it = ius.iterator();		while (it.hasNext()) {			IInstallableUnit iu = (IInstallableUnit) it.next();			if ((iu.getId().equals("com.dcns.rsm.rda") && iu.getVersion().equals(new Version("5.1.0.v20100112"))) || (iu.getId().equals("com.dcns.rsm.profile.equipment") && iu.getVersion().equals(new Version("1.2.2.v20100108"))) || (iu.getId().equals("com.dcns.rsm.profile.gemo") && iu.getVersion().equals(new Version("3.7.2.v20100108"))) || (iu.getId().equals("com.dcns.rsm.profile.system") && iu.getVersion().equals(new Version("4.2.2.v20100112")))) {				pcr.addInstallableUnits(new IInstallableUnit[] {iu});			}		}		return pcr;	}}